customStyle:
#branding {
  position: absolute !important;
  top: 30px !important;
  width: 240px !important;
  margin: auto;
  bottom: auto !important;
}

.promptumenu_window {
  margin-top: 18px;
  margin-bottom: 10px;
}

.app-content {
  margin-top: -12px !important;
  margin-bottom: 10px !important;
}

body {
  position: absolute;
  width: 100%;
  height: 100%;
  background-size: 100% 100% !important;
}

.icon {
  width: 56px !important;
  height: 56px !important;
}

.campuseai-Info {
  color: #fff;
  white-space: nowrap !important;
  text-transform: uppercase;
  text-shadow: 1px 1px 2px #000;
}

#homedata .campuseai-Info {
  width: 56px !important;
  height: 33px !important;
}

.dockicon:after {
  bottom: 0px !important;
}

.dockicon {
  margin-bottom: 25px !important;
}

.blockUI i {
  color: white !important;
}

.promptumenu_nav a.active {
  background: #fff;
  border-radius: 50%;
  width: 10px;
  height: 10px;
}

.promptumenu_nav a {
  background: #ad1a1a;
  border-radius: 50%;
  width: 10px;
  height: 10px;
}

.promptumenu_window {
  overflow: visible !important;
}

.promptumenu_nav {
  margin-bottom: -120px;
}

.app-body {
  padding-bottom: 0px !important;
  padding-top: 63px !important;
}

.navbar-app,
.sidebar,
#mainSidebar {
  background: rgb(168, 12, 12) !important;
  /* Old browsers */
  background: -moz-linear-gradient(top, rgba(168, 12, 12, 1) 0%, rgba(0, 0, 0, 1) 100%) !important;
  /* FF3.6+ */
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, rgba(168, 12, 12, 1)), color-stop(100%, rgba(0, 0, 0, 1))) !important;
  /* Chrome,Safari4+ */
  background: -webkit-linear-gradient(top, rgba(168, 12, 12, 1) 0%, rgba(0, 0, 0, 1) 100%) !important;
  /* Chrome10+,Safari5.1+ */
  background: -o-linear-gradient(top, rgba(168, 12, 12, 1) 0%, rgba(0, 0, 0, 1) 100%) !important;
  /* Opera 11.10+ */
  background: -ms-linear-gradient(top, rgba(168, 12, 12, 1) 0%, rgba(0, 0, 0, 1) 100%) !important;
  /* IE10+ */
  background: linear-gradient(to bottom, rgba(168, 12, 12, 1) 0%, rgba(0, 0, 0, 1) 100%) !important;
  /* W3C */
  filter: progid: DXImageTransform.Microsoft.gradient( startColorstr='#a80c0c', endColorstr='#000000', GradientType=0) !important;
  /* IE6-9 */
}

.blockUI {
  margin: auto !important;
  left: 0px !important;
  right: 0px !important;
  width: auto !important;
}

#homedata .campuseai-Info {
  width: 76px !important;
  margin-left: -10px;
}

@media (min-width: 320px) and (max-width: 480px) {
  .icon {
    width: 56px !important;
    height: 56px !important;
  }
  #homedata .campuseai-Info {
    width: 76px !important;
    margin-left: -10px;
  }
}

@media (min-width: 320px) and (max-width: 568px) {
