    font-size: 12px;
    margin-top: 2px;
    margin-left: -10px;
    margin-right: -10px;
    /*margin-left:-10px;*/
  }
}

@media (min-width: 768px) {
  #branding {
    width: 50% !important;
    margin: auto;
  }
  .icon {
    width: 102px !important;
    height: 102px !important;
  }
  #homedata .campuseai-Info {
