pages.pageprocessor:
function pageprocessorBRYHEALTHPASS(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  if (window.device) {
    $.blockUI();
  }
  var iabRef;
  if ($.jStorage.get('username') === null || $.jStorage.get('username') === "") {
    iabRef = window.open(pagedef.url, "_blank", "location=no,hidden=no,enableViewportScale=yes,clearcache=yes");
    window.location.href = "index.html#home";
    var hideBlockUi = function() {
      $.unblockUI();
      iabRef.show();
    };
    var iabClose = function(data) {
      $.unblockUI();
      iabRef.removeEventListener("exit", iabClose);
      window.location.href = "index.html#home";
    };
    iabRef.addEventListener("exit", iabClose);


    var loadStop = function(data) {
      //window.location.href = "index.html#home";
      hideBlockUi();
      iabRef.removeEventListener("loadstop", loadStop);
    };
    iabRef.addEventListener("loadstop", loadStop);
  } else {
    $rootScope.getAPI($rootScope.middlewareServerUrl + "/services/student/bannergetuserpidm", function(data) {
      var pidm = data.getuserpidm[0].SPRIDEN_PIDM;
      iabRef = window.open(pagedef.url + "?p_mobile_id=" + pidm, "_blank", "location=no,hidden=no,enableViewportScale=yes");
      window.location.href = "index.html#home";
    });
  }



}

