function pageprocessorFinalGradeB1(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);

    $rootScope.getAPI($rootScope.middlewareServerUrl + "/services/student/bannerhold", function(data) {
        var hasMidtermHolds = false;
        var GradesHolds = new Array('AD', 'BC', 'BG', 'P1', 'P2', 'SA');
        for (i = 0; i < data.hold.length; i++) {
            if (GradesHolds.indexOf(data.hold[i].HOLD_CODE) != -1) {
                hasMidtermHolds = true;
                break;
            }
        }
        if (hasMidtermHolds == true) {
            if (window.device) {
                var doNothing = function() {};
                navigator.notification.alert("Oops! You have " + data.hold[i].HOLD_CODE + " hold on your record.", doNothing, 'Alert', 'Ok');
            } else {
                apprise("Oops! You have " + data.hold[i].HOLD_CODE + " hold on your record.", {
                    'verify': false,
                    'textYes': "Ok"
                }, function(r) {
                    $rootScope.$apply(function() {
                        $location.path("/home");
                    });
                });
            }
        } else {
            $rootScope.getAPI($rootScope.middlewareServerUrl + pagedef.endpoint, function(data) {
                $scope.gradesdropdown = data.dropdown;
                if (data.dropdown.length == 1) {
                    $scope.selterm = data.dropdown[0].TERM_CODE;
                    $scope.termselect();
                }
            });
        }

    });


    $scope.termselect = function(termcode) {
        $.blockUI();
        $scope.finalgrade = null;
        $rootScope.getAPI($rootScope.middlewareServerUrl + "/services/student/bannerfinalgrade/termCode=" + $scope.selterm, function(data) {
            $scope.finalgrade = data.finalgrade;
            var hasFinalGrade = false;
            for (i = 0; i < data.finalgrade.length; i++) {
                if (data.finalgrade[i].FINAL_GRADE) {
                    hasFinalGrade = true;
                    break;
                }
            }
            if (hasFinalGrade == false) {
                var msg = "You have no Final Grades record for " + $("#FGrd option:selected").text() + " term";
                if (window.device) {
                    navigator.notification.alert(msg, null, 'Alert', 'Ok');
                } else {
                    apprise(msg, {
                        'verify': false,
                        'textYes': "Ok"
                    }, function(r) {

                    });
                }
            }
        });
    }
}

