function pageprocessorBryUCancelledClasses(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  $scope.feeds = [];
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  // This creates the loading screen
  $.blockUI();
  // Kryptos RSS reader if they stop supporting this we will need to find an alternative
  var rssreaderUrl = "https://kryptosda.kryptosmobile.com/kryptosds/rss/rssreader";
  var param = {
    "url": pagedef.url
  };
  // After the page is loaded add today's date in a nice blue box.
  $scope.$on('$viewContentLoaded', function() {
    var options = {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    };
    $("#appContent").prepend('<div id="today-date" margin-top:"20px">' + new Date().toLocaleString('en-US', options) + '</div>');
  });
  // Post to get the data from the reader/rss url
  $http.post(rssreaderUrl, param, '').success(function(data, status, headers, config) {
    // If the reader failed catch the exception and print out an error
    try {
      // There are no entries 
      if (data.feed.entries.length == 0) {
        $.unblockUI();
        $('#appContent').append('<p class="no-classes">No classes are cancelled.</p>');
      } else {
        $scope.feeds = data.feed.entries;
        $.unblockUI();
      }
    } catch {
      $('#appContent').append('<p class="no-classes">An issue has occured.</br> Please contact Laptop Central</br><a href="mailto:laptopcentral@bryant.edu" style="font-size:16px">laptopcentral@bryant.edu</a></p>');
      $.unblockUI();
    }
  }).error(function(error, status, header, config) {
    // Doesn't seem to get here but just in case
    $.unblockUI();
    $('#appContent').append('<p class="no-classes">An issue has occured.</br> Please contact Laptop Central</br><a href="mailto:laptopcentral@bryant.edu" style="font-size:16px">laptopcentral@bryant.edu</a></p>');
  });
  $scope.convertToDate = function(stringDate) {
    var dateOut = new Date(stringDate);
    dateOut.setDate(dateOut.getDate());
    return dateOut;
  };

}

