function pageprocessorEvents(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  $.blockUI();
  var url = pagedef.url;
  if (window.device) {} else {
    //url = "/websimulator/xml?url=" + pagedef.url;
  }
  $http.get(url, {
    "responseType": "xml",
    "transformResponse": function(data) {
      var jsonData = $.xml2json(data);
      return jsonData;
    }
  }).success(function(results) {
    var data = [];
    var index = 0;
    $.each(results.channel.item, function(key, val) {
      index = index + 1;
      data.push({
        "link": val.link,
        "displaydate": new Date(val.pubDate).toDateString() + " " + new Date(val.pubDate).toLocaleTimeString(),
        "date": new Date(val.pubDate).getTime().toString(),
        "title": val.title,
        "description": val.description,
        "index": index
      });
    });
    $rootScope.Eventsdata = data;
    $("#appContent").append($(pagedef.pageTemplate));
    $("#EventsListData").eventCalendar({
      jsonData: data,
      showDescription: true,
      dataTemplate: pagedef.datatemplate
    });
    $.unblockUI();
  }).error(function(data, status, headers, config) {
    $.unblockUI();
    console.log(data);
    console.log(status);
    console.log(headers);
    console.log(config);
    apprise("Error occured. Please try again later.", {
      "verify": false,
      "textYes": "Ok"
    }, function(r) {
      $window.history.back();
    });
  });
}

function pageprocessorDirectory(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    $scope.resultfound = true;
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $('#srch').on('submit', function(event) {
        event.preventDefault();
        $scope.search();
        $scope.$digest();
        $("#srchbtn").focus();
        return false;
    });
    $scope.fetched = false;
    $scope.filterresult = function() {
        var searchTxt = $("#Directorytxt").val();
        $rootScope.Directoryval = searchTxt;
        var results = [];
        var patt = new RegExp(searchTxt, "i");
        var filterParams = pagedef.searchfields.split(",");
        $.each($scope.DirectoryList.DATA_RECORD, function(index, val) {
            var found = false;
            for (var i = 0; i < filterParams.length; i++) {
                if (val["" + filterParams[i] + ""].match(patt)) {
                    found = true;
                    break;
                }
            }
            if (found) {
                results.push(val);
            }
        });
        $rootScope.DirectoryResult = results;
        if (results.length > 0) {
            $scope.resultfound = true;
        } else {
            $scope.resultfound = false;
        }
    };
    $scope.search = function() {
        if ($scope.fetched == false) {
            $.blockUI();
            var url = pagedef.url;
            if (window.device) {} else {
                url = "/websimulator/xml?url=" + pagedef.url;
            }
            var xhr = $.ajax({
                url: url,
                dataType: "xml",
                async: true,
                data: {},
                success: function(resp) {
                    try {
                        var results = $.xml2json(resp);
                        $scope.fetched = true;
                        $scope.DirectoryList = results;
                        $scope.filterresult();
                        $scope.$apply();
                        $.unblockUI();
                    } catch (E) {
                        alert("Exception : " + E);
                    }
                },
                error: function(e) {
                    alert("Error : " + e);
                    $.unblockUI();
                    apprise("Error occured. Please try again later.", {
                        "verify": false,
                        "textYes": "Ok"
                    }, function(r) {
                        $window.history.back();
                    });
                }
            });
        } else {
            $scope.filterresult();
        }
    }
}

