        } else {
            $rootScope.getAPI($rootScope.middlewareServerUrl + pagedef.endpoint, function(data) {
                $scope.gradesdropdown = data.dropdown;
            });
        }

    });


    $scope.termselect = function(termcode) {
        $scope.midterm = null;
        $rootScope.getAPI($rootScope.middlewareServerUrl + "/services/student/bannermidtermgrade/termCode=" + $scope.selterm, function(data) {
