function pageprocessorgclasses(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  $scope.data = {};
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $.blockUI();
  var rssreaderUrl = "https://kryptosda.kryptosmobile.com/kryptosds/rss/rssreader";
  var param = {
    "url": pagedef.url
  };
  console.log(param);
  $http.post(rssreaderUrl, param, '').success(function(data, status, headers, config) {
